import React, { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router-dom";
import Chart from "./chart";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import Error from "./Error"

const KEY = process.env.REACT_APP_OPENWEATHERMAP_API_KEY;

function Forecast() {
  const { cityName } = useParams();
  const history = useHistory();
  const [dataToDraw, setDataToDraw] = useState([]);
  const [hasError, setError] = useState(false);
  const [isLoading, setLoading] = useState(false);

  const fetchDetails = async (name) => {
    try {
      const data = await fetch(
        `https://api.openweathermap.org/data/2.5/forecast?q=${name}&units=metric&appid=${KEY}`
      );
      let fiveDaysData = await data.json();
      console.log(fiveDaysData);
      let chartData = [];

      for (let i = 0; i < fiveDaysData.list.length; i++) {
        chartData = [
          ...chartData,
          {
            name: fiveDaysData.list[i].dt_txt,
            "°C": fiveDaysData.list[i].main.temp,
          },
        ];
      }

      return chartData;
    } catch (error) {
      setError(true);
      console.error(error);
    }
  };

  function navigateToHome() {
    history.push("/");
  }

  useEffect(() => {
    fetchDetails(cityName).then((data) => setDataToDraw(data));
  }, []);

  return (
    <Card>
      <CardContent>
        <Typography variant="h5" component="h2">
          {cityName}
        </Typography>

        <Typography>



          {hasError && (
            <Error message="Oppsss"/>
          )}

            <Chart dataToDraw={dataToDraw}></Chart>

          <button className="btn btn-primary" onClick={navigateToHome}>
            Home
          </button>
        </Typography>
      </CardContent>
    </Card>
  );
}

export default Forecast;
