import React from "react";
import {
  LineChart,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  Line,
  Legend,
} from "recharts";


function Chart({ dataToDraw }) {

  return (
    <LineChart
    options={{
        title: 'Age vs. Weight comparison',
        hAxis: { title: 'Age', minValue: 0, maxValue: 15 },
        vAxis: { title: 'Weight', minValue: 0, maxValue: 15 },
        legend: 'none',
        animation: {
          startup: true,
          easing: 'linear',
          duration: 1500,
        },
        enableInteractivity: false,
      }}


      width={600}
      height={300}
      data={dataToDraw}
      margin={{ top: 5, right: 30, left: 20, bottom: 5 }}

    >
      <XAxis dataKey="name" />
      <YAxis />
      <CartesianGrid strokeDasharray="3 3" />
      <Tooltip />
      <Legend />
      <Line

        type="monotone"
        dataKey="Â°C"
        stroke="#8884d8"
        activeDot={{ r: 8 }}
      />
    </LineChart>
  );
}

export default Chart;
