import React, { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router-dom";
import Chart from "./chart";

const KEY = process.env.REACT_APP_OPENWEATHERMAP_API_KEY;

function Forecast() {
  const { cityName } = useParams();
  const history = useHistory();
  const [dataToDraw, setDataToDraw] = useState([]);
  const [countryName, setCountryName] = useState("");
  const [isLoading, setLoading] = useState(false);
  const [hasError, setError] = useState(false);

  const fetchDetails = async (name) => {
    try {

      const data = await fetch(
        `https://api.openweathermap.org/data/2.5/forecast?q=${name}&units=metric&appid=${KEY}`
      );
      setLoading(true);
      let fiveDaysForecast = await data.json();
      console.log(fiveDaysForecast);
      let dailyData = [];
      for (let i = 0; i < fiveDaysForecast.list.length; i++)
      dailyData = [
          ...dailyData,
          {
            name: fiveDaysForecast.list[i].dt_txt.substr(0, 10),
            uv: fiveDaysData.list[i].main.temp,
          },
        ];

      setLoading(false);
      return chartData;
    } catch (error) {
      setError(true);
      console.error(error);
    }
  };

  function navigateToHome() {
    history.push("/");
  }

  useEffect(() => {
    fetchDetails(cityName).then((data) => setDataToDraw(data));
  }, []);

  return (
    <>
      <div>
        {cityName} , {countryName}
      </div>
      <div>{!isLoading && <Chart dataToDraw={dataToDraw}></Chart>}</div>
      <div>{hasError && <p>Sorry can't load chart</p>}</div>
      <button className="btn btn-primary" onClick={navigateToHome}>
        Home
      </button>
    </>
  );
}

export default Forecast;
